import { INode, INodeData, INodeParams } from '../../../src/Interface'

class TextProcessor implements INode {
    label: string
    name: string
    version: number
    type: string
    icon: string
    category: string
    description: string
    baseClasses: string[]
    inputs: INodeParams[]

    constructor() {
        this.label = 'Text Processor'
        this.name = 'textProcessor'
        this.version = 1.0
        this.type = 'TextProcessor'
        this.icon = 'text'
        this.category = 'text_processing'
        this.description = 'Process and transform text input with various operations'
        this.baseClasses = [this.type]
        this.inputs = [
            {
                label: 'Input Text',
                name: 'inputText',
                type: 'string',
                placeholder: 'Enter text to process...'
            },
            {
                label: 'Operation',
                name: 'operation',
                type: 'options',
                options: [
                    { label: 'Uppercase', name: 'uppercase' },
                    { label: 'Lowercase', name: 'lowercase' },
                    { label: 'Reverse', name: 'reverse' },
                    { label: 'Length', name: 'length' }
                ],
                default: 'uppercase'
            }
        ]
    }

    async init(nodeData: INodeData, _: string, options: { componentNodes: any }): Promise<string> {
        const inputText = nodeData.inputs?.inputText as string
        const operation = nodeData.inputs?.operation as string || 'uppercase'

        if (!inputText) {
            throw new Error('Input text is required')
        }

        try {
            let result: string

            switch (operation) {
                case 'uppercase':
                    result = inputText.toUpperCase()
                    break
                case 'lowercase':
                    result = inputText.toLowerCase()
                    break
                case 'reverse':
                    result = inputText.split('').reverse().join('')
                    break
                case 'length':
                    result = inputText.length.toString()
                    break
                default:
                    result = inputText
            }

            return result
        } catch (error) {
            throw new Error(`Text processing failed: ${error.message}`)
        }
    }
}

module.exports = { nodeClass: TextProcessor }
